$(document).ready(function(){
    AOS.init({disable:"mobile"})

    // Alert users to a non existing date when submitting
    $("#subscribe").submit(function(e) {
        e.preventDefault(); 
        var $emailBox = $("#email-box");
        if ($emailBox.val().length > 0) {
            alert("Thanks for joining our news letter!  You'll get an email on June 31st");
        }
        $emailBox.val("");
    })
});